    def balik_dashboard(self):
        import dasboardpendonor
        self.dashboardWindow = QtWidgets.QMainWindow()
        self.dashboardUI = dasboardpendonor.Ui_DonorDashboard()
        self.dashboardUI.setupUi(self.dashboardWindow)
        self.dashboardUI.set_pendonor(self.id_pendonor)
        self.dashboardWindow.show()
        self.window.close()
    
    def masuk_jadwal_donor(self):
        import jadwal
        self.jadwal_window = QtWidgets.QMainWindow()
        self.jadwal_ui = jadwal.Ui_JadwalDonor()
        self.jadwal_ui.setupUi(self.jadwal_window)
        self.jadwal_ui.set_pendonor(self.id_pendonor)
        self.jadwal_window.show()
        self.window.close()
        
    def daftar_donor(self):
        if not self.id_pendonor:
            QMessageBox.warning(
                self.window,
                "Error",
                "Anda belum login sebagai pendonor."
            )
            return

        berat = self.inputBerat.text().strip()

        # validasi input
        if not berat.isdigit():
            QMessageBox.warning(
                self.window,
                "Error",
                "Berat badan harus berupa angka."
            )
            return

        berat = int(berat)

        if berat < 45:
            QMessageBox.warning(
                self.window,
                "Error",
                "Berat badan minimal 45 kg."
            )
            return

        # simpan ke database
        db = PendaftaranDonor()

        # cek apakah masih ada pendaftaran aktif
        if db.cek_daftar_aktif(self.id_pendonor) > 0:
            QMessageBox.information(
                self.window,
                "Info",
                "Anda sudah memiliki pendaftaran donor yang menunggu."
            )
            return

        db.simpan_pendaftaran(self.id_pendonor, berat)

        QMessageBox.information(
            self.window,
            "Berhasil",
            "Pendaftaran donor berhasil dikirim.\nSilakan menunggu konfirmasi PMI."
        )

        # optional: reset form
        self.inputBerat.clear()
        self.inputRiwayat.clear()


