    def load_stok_data(self):
        self.valueTotalStok.setText(f"{get_total_stok()} Kantong")
        self.valueKritis.setText(f"{get_stok_kritis()} Golongan")
        self.valueKadaluarsa.setText(f"{get_stok_kadaluarsa()} Kantong")
        rows = get_all_stok()
        self.tableStok.setRowCount(len(rows))
        for i, row in enumerate(rows):
            for j, value in enumerate(row):
                self.tableStok.setItem(i, j, QtWidgets.QTableWidgetItem(str(value)))
        

